import React, { useState } from 'react';
import './css/table.css'; // Importa o arquivo de estilo CSS

function Table() {
  const [colorIndex, setColorIndex] = useState(0); // Define o estado do índice de cor inicialmente como 0

  const rows = [
    { id: 1, name: 'John', age: 25 },
    { id: 2, name: 'Mary', age: 30 },
    { id: 3, name: 'Peter', age: 35 }
  ]; 

  const getColorClass = () => {
    return colorIndex % 2 === 0 ? 'even' : 'odd';
  };
 

  return (
    <table>
      <thead>
        <tr>
          <th>Nome</th>
          <th>CPF</th>
          <th>E-mail</th>
          <th>Senha</th>
        </tr>
      </thead>
      <tbody>
        {rows.map((row) => (
          <tr key={row.id} className={getColorClass()}>
            <td>{row.id}</td>
            <td>{row.name}</td>
            <td>{row.age}</td>
          </tr>
          // Usa a classe de cor atual para a linha e define uma função de clique para a linha
          // O atributo "key" deve ser definido para evitar um aviso de console
        ))}
      </tbody>
    </table>
  );
}

export default Table;
