import React, { useState } from 'react';
import './css/table.css'; // Importa o arquivo de estilo CSS

function Table() {
  const [colorIndex, setColorIndex] = useState(0); // Define o estado do índice de cor inicialmente como 0

  const rows = [
    { id: 1, name: 'John', CPF: "000.111.222-44", password: "123" },
    { id: 2, name: 'Alice', CPF: "111.222.333-55", password: "456" },
    { id: 3, name: 'Bob', CPF: "222.333.444-66", password: "789" },
    { id: 4, name: 'Emily', CPF: "333.444.555-77", password: "abc" },
    { id: 5, name: 'David', CPF: "444.555.666-88", password: "def" },
    { id: 6, name: 'Julia', CPF: "555.666.777-99", password: "ghi" },
    { id: 7, name: 'Lucas', CPF: "666.777.888-00", password: "jkl" }
  ]; 

  const getColorClass = () => {
    return colorIndex % 2 === 0 ? 'even' : 'odd';
  };
 

  return (
    <table>
      <thead>
        <tr>
          <th>Nome</th>
          <th>CPF</th>
          <th>E-mail</th>
          <th>Senha</th>
        </tr>
      </thead>
      <tbody>
        {rows.map((row) => (
          <tr key={row.id} className={getColorClass()}>
            <td>{row.id}</td>
            <td>{row.name}</td>
            <td>{row.age}</td>
            <td>{row.password}</td>
          </tr>
          // Usa a classe de cor atual para a linha e define uma função de clique para a linha
          // O atributo "key" deve ser definido para evitar um aviso de console
        ))}
      </tbody>
    </table>
  );
}

export default Table;
